mapper.insert:
INSERT INTO board( b_idx, b_title, b_content, b_writer, b_date
)VALUES( #{bId}, #{bTitle}, #{bContent}, #{bWriter}, #{bDateTime}
)
SELECT LAST_INSERT_ID()

UPDATE board
SET
	b_title= #{bTitle},
	b_content= #{bContent},
	b_date = #{bDateTime}

WHERE b_idx = #{bId}

mapper.select:
select b_idx as bId, c_writer, c_content, c_no, c_date
from comment
where b_idx = #{bId}

select board.b_idx as bId, fileName
from board LEFT JOIN file ON board.b_idx = file.b_idx
where board.b_idx = #{bId}
